import React from 'react'
import QrReader from 'react-qr-reader';


class PunchForm extends React.Component {
    constructor(){
        super();
        this.state = {
          location: ''
        }
      }
    
    
    handleScan = (data) => {
        if (data) {
            this.setState({location: data});
        } else {
            this.setState({location: ''});
        }
      }
    handleError = (err) => {
        console.error(err)
    }

    onRouteChange = () => {
        if (this.state.location !== ''){
            const info = {
                location: this.state.location,
                date: getDate(),
                in_out: this.props.punch.in_out
            }
            this.props.onSubmit(info);
            this.props.onRouteChange('punchrecipt');
        }
        
    }

    getDate = () => {
        const today = new Date();
        const date = (`${today.getFullYear()}/${(today.getMonth()+1)}/${today.getDate()}`);
        return date;
    }

    render(){
    const { in_out, date} = this.props.punch;
    const curDate = this.getDate();
    return(
        <div>
            <QrReader
                className = 'center'
                delay={200}
                onError={this.handleError}
                onScan={this.handleScan}
                style={{ width: '500px' }}
            />
            <div className='fw6 lh-copy f3' >
                <label className="db">{`Place: ${this.state.location}`}</label>
                <label className="db">User: Mikan</label>
                <label className="db">{`Date: ${curDate}`}</label>
                <label className="db">{`In/Out: ${in_out}`}</label>
            </div>
            <div className="lh-copy mt3 center">
                <input 
                    className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" 
                    type="submit" 
                    value="Punch"
                    onClick={this.onRouteChange}
                />
            </div>
        </div>    
    )
    }
}

export default PunchForm;
