import React from 'react';
import Submitbtn from '../Submitbtn';

const Menu = ({onRouteChange, punchIn_Out, user}) => {
    let admin;
    if (user.admin){
        admin = (
            <div>
                <Submitbtn 
                    style={{width: '80%'}}
                    value="Register New User"
                    onClick={() => onRouteChange('register')} 
                />
                <Submitbtn
                    style={{width: '80%'}}
                    value="Edit Users"
                    onClick={() => onRouteChange('editusers')} 
                />
                <Submitbtn 
                    style={{width: '80%'}}
                    value="Locations"
                    onClick={() => onRouteChange('editlocations')} 
                />
            </div>
        )
    }
    return(
        <div className='br3 pa2 ba  mv4 w-100 w-50-m w-25-1 mw6 shadow-5 center' >
        
            <div className='br3 w-100'>
                <div className='w-100 black-80 '>
                    <Submitbtn 
                        style={{width: '80%'}}
                        value="Punch Out"
                        onClick={() => {
                            onRouteChange('punchform');
                            punchIn_Out('Out');
                        }} 
                    />
                    <Submitbtn 
                        style={{width: '80%'}}
                        value="Punch In"
                        onClick={() => {
                            onRouteChange('punchform');
                            punchIn_Out('In');
                            }}
                    />
                    <Submitbtn 
                        style={{width: '80%'}}
                        value="History"
                        onClick={() => onRouteChange('history')} 
                    />
                    {admin}
                </div>
            </div>
        </div>
    )
}

export default Menu;
