import React from 'react';
import QrReader from 'react-qr-reader';
import Inputbar from '../EditUsers/Inputbar';
import Submitbtn from '../Submitbtn';

class Add extends React.Component {
    constructor(){
        super();
        this.state = {
           newname: '',
           newcode: '',
           nedaddress: '',
           active: true
        }
    }

    handleError = (err) => {
        console.error(err)
    }

    handleScan = (data) => {
        if (data) {
            this.setState({newcode:data})
        }
        console.log(data)
    }
    render(){
        return (
            <div> 
                <label className="db fw6 lh-copy f3" > Scan New code here </label>
                <QrReader
                    className = 'center'
                    delay={200}
                    onError={this.handleError}
                    onScan={this.handleScan}
                    style={{ width: '500px' }}
                />
                <form className="measure center">
                    <div id="sign_up" className="ba b--transparent ph0 mh0">
                        <label className="f4 fw6 ph0 mh0">{"Add Location"}</label>
                        <Inputbar label='Name' type="text" id="newname" placeholder= "New name" />

                        <Inputbar label='Password/Code' type="text" id="newpassword" placeholder= "New password/code" />

                        <Inputbar label='Address' type="text" id="newaddress" placeholder= "New address" />

                        <Submitbtn value="Add Location" onClick={this.onAddLocation} />
                    </div>
                </form>
            </div>
        )
    }
}

export default Add;
