import React from 'react';
import Image from './image.jpg';
import QrReader from 'react-qr-reader';

const PunchForm = ({in_out}) => {
    
    handleScan = data => {
        if (data) {
          console.log(data)
        }
      }
    handleError = err => {
        console.error(err)
    }

    return(
        <div>
            <QrReader
                delay={300}
                onError={handleError}
                onScan={handleScan}
                style={{ width: '50%' }}
            />
        </div>    
    )
}

export default PunchForm;



