import React from 'react';

class Manualpunch extends React.Component {
    constructor(){
        super();
        this.state = {
            locationarr: [],
            adminlist: [],
        }
    }

    getadminlist = () =>{
        fetch(`https://mikan-app-api.herokuapp.com/loadusers`, {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({
            id: this.props.user.id,
            admin: this.props.user.admin
        })
    })
    .then(response => response.json())
    .then(usersarray =>{
        this.setState({adminlist: usersarray});
    })
    .catch(err => console.log(err));
    }

    loadlocarr = () =>{
        fetch(`https://mikan-app-api.herokuapp.com/locationsload`, {
            method: 'GET',
            headers: {'Content-Type': 'application/json'},
        })
        .then(response => response.json())
        .then(locarr =>{
            if (locarr[0].id){
            this.setState({locationarr: locarr})
            } else {
                console.log(locarr)
            }
        })
        .catch(err => console.log(err));
    }

    componentDidMount() {
        this.loadlocarr();
        this.getadminlist();
        console.log(this.state.adminlist);
    }

    render() {
        const { adminlist, locationarr } = this.state;

        return(
            <div className='flex justify-center mb3 bb table-cont '>
            <div id='select-user' className='ssbtn m4 flex w-40 justify-center '>
                <select id='select_user' className='w-100 hover-bg-black hover-white bg-transparent b ba b--black' >
                    {
                        adminlist.map(function(data,i){
                            return(
                                //NEED TO ADD KEY VALUES THAT ARE UNIQUE
                                <option className='pa2' value={data}>{data.name}</option>
                            )
                        })
                    }
                </select>
                </div>
                <select id='select_user' className='w-100 hover-bg-black hover-white bg-transparent b ba b--black' >
                    {
                        locationarr.map(function(data,i){
                            return(
                                //NEED TO ADD KEY VALUES THAT ARE UNIQUE
                                <option className='pa2' value={data}>{data.name}</option>
                            )
                        })
                    }
                </select>


                <div className=' w-20 m4'>
                    <label className='b ssftn5' >Date: </label>
                    <input className='b ba b--black bg-transparent pointer ssftn5 dib hover-bg-black hover-white w-100' type="date" id="start" name="search-start" required pattern="[0-9]{4}-[0-9]{2}-[0-9]{2}"/>
                </div>
                <div className='w-20 m4 '>
                    <label className='b ssftn5' >Time </label>
                    <input className='b ba b--black bg-transparent pointer ssftn5 dib hover-bg-black hover-white w-100' type="time" id="timr" name="search-time" required/>
            </div>
            </div>
        )
    }
}

export default Manualpunch;
