import React from 'react';

const Edit = ({ singleuser, onReload, route }) => {
    
    const onUpdate = () => {
        let change = {
            id: singleuser.id
        }
        const newname = document.getElementById('newname').value;
        const newpass = document.getElementById('newpassword').value;
        const box = document.getElementById('newadmin');
        if (!newname.includes(' ') && newname.length > 0){
           change['name'] = newname;
        } 
        if (!newpass.includes(' ') && newpass.length > 0){
            change['pass'] = newpass;
         }
        if (box.checked) {
            change['admin'] = 'yes';
        } else {
            change['admin'] = 'no';
        }
        submitUpdate(route, change);
        
    }

    const onDeleteuser = () => {
        let change = {
            id: singleuser.id
        }
        const box = document.getElementById('deletebox');
        if (box.checked){
            submitUpdate(route, change)
        } else {
            onReload('editusers');
        }
    }


    const submitUpdate = (type, data ) => {
        console.log(data);
        fetch(`http://localhost:3000/edit${type}`, {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify(
           data
        )
        })
        .then(response => response.json())
        .then(updateduser =>{
            console.log(updateduser)
            onReload('editusers');
        })
        .catch(err => console.log(err));
    }

  
    if (route === 'user'){
        return(
            <div>
            <main className="pa4 black-80">
                <form className="measure center">
                    <div id="sign_up" className="ba b--transparent ph0 mh0">
                        <label className="f4 fw6 ph0 mh0">{`Edit ${singleuser.name}`}</label>
                        <div className="mt3">
                            <label className="db fw6 lh-copy f6" >Name</label>
                            <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="text" name="Name"  id="newname" placeholder= {`Updated users name`}  />
                        </div>
                        <div className="mv3">
                            <label className="db fw6 lh-copy f6" >Password</label>
                            <input className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="password" name="password"  id="newpassword" placeholder='Updated users password' />
                        </div>
                        <label className="pa0 ma0 lh-copy f6 pointer"><input id='newadmin' type="checkbox" /> Admin User?</label>
                    </div>
                    <div className="">
                        <input className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" type="reset" value="Update User" onClick={onUpdate} />
                    </div>
                </form>
            </main>     
        </div>
        )
    }
    
    else if (route === 'delete') {
        return(
            <div className='shadow-5 w-50 center'>
                <main className="pa4 black-80">
                    <form className="measure center">
                        <div id="sign_up" className="ba b--transparent ph0 mh0">
                            <label className="f4 fw6 ph0 mh0">{`User: ${singleuser.name}`}</label>
                            <div className="mt3">
                                <label className="db fw6 lh-copy f6" >Update form </label>
                                <label className="db fw6 lh-copy f6" >{`(Check box and submit if you are sure you want to delete ${singleuser.name})`} </label>
                                <label className="pa0 ma2 lh-copy f6 pointer"><input  id='deletebox' type="checkbox" /> Delete User?</label>
                                <input className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" type="reset" value="Update " onClick={onDeleteuser} />
                            </div>
                        </div>
                    </form>
                </main>         


            </div>
        )

    }
}

export default Edit;
