import React from 'react';

const Edit = ({ singleuser }) => {
    
    const onUpdateName = () => {
        //CREATE FETCH FUNCTION TO GET THE USER WE ARE LOOKING AT AND CHANGE THE NAME AS LONG AS NO ONE ELSE HAD THAT NAME
        const newname = document.getElementById('newusername').value;
        submitUpdate('name', newname); 
    }
    const onUpdatePassword = () => {
        const newpassword = document.getElementById('newpassword').value;
        submitUpdate('password', newpassword); 
    }
    const onUpdateAdmin = () => {
        const box = document.getElementById('newadmin');
        let newadmin;
        if (box.checked){
            newadmin = true;
        } else {
            newadmin = false;
        }

        submitUpdate('admin', newadmin); 
    }

    const submitUpdate = (type, data ) => {
        console.log(data)
        fetch(`http://localhost:3000/edit${type}`, {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({
            id: singleuser.id,
            change: data,
        })
    })
    .then(response => response.json())
    .then(updateduser =>{
        console.log(updateduser);
    })
    .catch(err => console.log(err));
    }


    return(
        <div className='shadow-5 w-50 center'>
            <main className="pa4 black-80">
                <form className="measure center">
                    <div id="sign_up" className="ba b--transparent ph0 mh0">
                        <label className="f4 fw6 ph0 mh0">{`User: ${singleuser.name}`}</label>
                        <div className="mt3">
                            <label className="db fw6 lh-copy f6" >New Name</label>
                            <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100 ma2" type="text" name="Name"  id="newusername" placeholder='Updated Name' />
                            <input className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" type="button" value="Update Name" onClick={onUpdateName} />

                        </div>
                        <div className="mv3">
                            <label className="db fw6 lh-copy f6" >New Password</label>
                            <input className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100 ma2" type="password" name="password"  id="newpassword" placeholder='Updated Password' />
                            <input className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" type="button" value="Update Password" onClick={onUpdatePassword} />
                        </div>
                        <label className="pa0 ma0 lh-copy f6 pointer"><input id='newadmin' type="checkbox"/> Admin User?</label>
                        <input className="ma3 b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" type="button" value="Update Admin" onClick={onUpdateAdmin} />
                    </div>
                </form>
            </main>         


        </div>
    )
}

export default Edit;
