import React from 'react';
import Singlepunch from '../Singlepunch';

class Lastpunch extends React.Component {
    constructor(){
        super();
        this.state = {
            latestpunch: {},
        }
    }

    getLastPunch = (user) => {
        fetch(`http://localhost:3000/latestpunch`, {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                id: user.id,
            })
        })
        .then(response => response.json())
        .then(resp =>{
            if (resp.id) {
                console.log(resp.date)
                return resp;
                
            } else {
                console.log(resp);
            }
        })
        .catch(err => console.log(err));
    }

    getAdminLastPunch = (adminuser) => {
        const {admin, id} = adminuser;


    }

    componentDidMount() {
        if (!this.props.user.admin){
            this.getAdminLastPunch(this.props.user);
        } else {
            let lastpunch = this.getLastPunch(this.props.user);
            this.setState({latestpunch:lastpunch})
            console.log(this.state.latestpunch)
        
        }
    }


    render() {
        if (this.state.latestpunch != {} ){
            return (
                <article class="mw5 center bg-lavender br3 pa3 pa4-ns mv3 ba b--black-10">
                    <div class="tc">
                        <h1 class="f4">Last Punch</h1>
                        <hr class="mw3 bb bw1 b--black-10" />
                    </div>
                        <label className="db">{`Last Punch`}</label>
                        <Singlepunch punch={this.state.latestpunch} />
                </article>
            )
        } else 
            return (<div></div>)
        
    }
}

export default Lastpunch;
