import React from 'react';


class EditUsers extends React.Component {
    constructor(){
        super();
        this.state = {
            usersArr: []
        }
      }
    
    // loadArr = () =>{
    //     fetch(`http://localhost:3000/editusers`, {
    //         method: 'POST',
    //         headers: {'Content-Type': 'application/json'},
    //         body: JSON.stringify({
    //             id: this.props.user.id,
    //             admin: this.props.user.admin,
    //         })
    //     })
    //     .then(response => response.json())
    //     .then(usersArray =>{
            
    //         this.setState({usersArr: usersArray})
           
    //     })
    //     .catch(err => console.log(err));
    // }
    
    ifAdmin = (user) =>{
        if (user){
            return 'True'
        }
        else{
            return 'False'
        }
    }

    
    render(){
        const { editUsersarray } = this.props;
        return(
            
            <div className='overflow-y-auto'> 
                
                <table className="" cellSpacing="0" id='historyTable'>
                        <thead className=''>
                            <tr className=''>
                                <th className=" ">ID</th>
                                <th className=" ">Name</th>
                                <th className=" ">Admin?</th>
                            </tr>
                        </thead>
                        <tbody className="">
                            {editUsersarray.map(function(data, i){
                                console.log(data.admin)
                                return(
                                    <tr className='' >
                                        <td key={data.id} className="">{data.id}</td>
                                        <td key={data.name} className="">{data.name}</td>
                                        <td key={i} className="">{`${data.admin}`}</td>
                                    </tr>
                                )
                            })}
                        </tbody>
                    </table>
                
            </div>
        )
    }
}

export default EditUsers;
