import React from 'react';

class Locations extends React.Component {
    constructor(){
        super();
        this.state = {
          
            locationarr: []
        }
      }
    

    editUsersArr = () =>{

        fetch(`http://localhost:3000/locationarr`, {
            method: 'GET',
            headers: {'Content-Type': 'application/json'},
        })
        .then(response => response.json())
        .then(locarr =>{
            if (locarr[0].id){
            this.setState({locationarr: locarr})
            } else {
                console.log(locarr)
            }
            
        })
        .catch(err => console.log(err));
      }

    componentDidMount() {
        this.editUsersArr();
        
    }

    
    render(){
        ;
        const { onRouteChange } = this;
        const { route, singleuser, userarr } = this.state;
        if (route === 'main'){
            return(
                
                <div className='table-cont' > 
                    
                    <table className="" cellSpacing="0" id='historyTable'>
                            <thead align='' className='' >
                                <tr className=''>
                                    <th className=" ">ID</th>
                                    <th className=" ">Name</th>
                                    <th className=" ">Admin?</th>
                                    <th colSpan='4' className=" ">Edit User</th>
                                </tr>
                            </thead>
                            <tbody className="">
                                {userarr.map(function(data, i){
                                    return(
                                        <tr className='' >
                                            <td key={'id'} className="">{data.id}</td>
                                            <td key={'name'} className="">{data.name}</td>
                                            <td key={'adminuser'} className="">{`${data.admin}`}</td>
                                            {/* <td key={'editname'}>
                                                <input 
                                                    className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6" 
                                                    type="button" 
                                                    value="Edit Name" 
                                                    onClick={() =>
                                                        onRouteChange("name", data)} />
                                            </td>
                                            <td key={'editpassword'}>
                                                <input 
                                                    className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6" 
                                                    type="button" 
                                                    value="Edit Password" 
                                                    onClick={() =>
                                                        onRouteChange("password", data)} />
                                            </td>
                                            <td key={'editadmin'}>
                                                <input 
                                                    className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6" 
                                                    type="button" 
                                                    value="Change Admin Access" 
                                                    onClick={() =>
                                                        onRouteChange("admin", data)} />
                                            </td>
                                            <td key={'deleteuser'}>
                                                <input 
                                                    className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6" 
                                                    type="button" 
                                                    value="Delete user" 
                                                    onClick={() =>
                                                        onRouteChange("delete", data)} /> */}
                                            {/* </td> */}
                                        </tr>
                                    )
                                })}
                            </tbody>
                        </table>
                    
                </div>
            )
        }
    
    }
}

export default Locations;
