import React from 'react';
import Edit from './Edit';


class EditUsers extends React.Component {
    constructor(){
        super();
        this.state = {
            route: 'main',
            singleuser: {}
        }
      }
    
    // loadArr = () =>{
    //     fetch(`http://localhost:3000/editusers`, {
    //         method: 'POST',
    //         headers: {'Content-Type': 'application/json'},
    //         body: JSON.stringify({
    //             id: this.props.user.id,
    //             admin: this.props.user.admin,
    //         })
    //     })
    //     .then(response => response.json())
    //     .then(usersArray =>{
            
    //         this.setState({usersArr: usersArray})
           
    //     })
    //     .catch(err => console.log(err));
    // }
    
    onRouteChange = (route, user) =>{
        this.setState({route: route, singleuser:user})
    }
    
    onReload = (route) => {
        this.setState({route: 'main', singleuser:{}})
        this.props.onRouteChange(route);
    }

    
    render(){
        const { editUsersarray } = this.props;
        const { onRouteChange } = this;
        const { route, singleuser } = this.state;
        if (route === 'main'){
            return(
                
                <div className='overflow-y-auto'> 
                    
                    <table className="" cellSpacing="0" id='historyTable'>
                            <thead className=''>
                                <tr className=''>
                                    <th className=" ">ID</th>
                                    <th className=" ">Name</th>
                                    <th className=" ">Admin?</th>
                                    <th className=" ">Edit User</th>
                                </tr>
                            </thead>
                            <tbody className="">
                                {editUsersarray.map(function(data, i){
                                    return(
                                        <tr className='' >
                                            <td key={'id'} className="">{data.id}</td>
                                            <td key={'name'} className="">{data.name}</td>
                                            <td key={'adminuser'} className="">{`${data.admin}`}</td>
                                            <td key={'edit'}>
                                                <input 
                                                    className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" 
                                                    type="button" 
                                                    value="Edit User" 
                                                    onClick={() =>
                                                        onRouteChange("edit", data)} />
                                            </td>
                                        </tr>
                                    )
                                })}
                            </tbody>
                        </table>
                    
                </div>
            )
        }
        else if(route === 'edit') {
            return(
                <Edit singleuser={singleuser}  onReload={this.onReload}/>
            )
        }
    }
}

export default EditUsers;
