        // }
    }

    

    reset = () => {
        document.getElementById('error').innerText = '';
    }
    render() {
        const message = document.getElementById('error');
        console.log(message)
       


        return (
            <div
                id = 'error'
                className='ma4'
            ></div>
        )
    }
}

export default Error;
