import React from 'react';


class EditUsers extends React.Component {
    constructor(){
        super();
        this.state = {
            usersArr: []
        }
      }
    
    loadArr = () =>{
        fetch(`http://localhost:3000/editusers`, {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                id: this.props.user.id,
                admin: this.props.user.admin,
            })
        })
        .then(response => response.json())
        .then(usersArray =>{
            console.log('good')
            this.setState({usersArr: usersArray})
           
        })
        .catch(err => console.log(err));
    }
    
    ifAdmin = (user) =>{
        if (user){
            return 'True'
        }
        else{
            return 'False'
        }
    }

    
    render(){
        this.loadArr()
        const { usersArr } = this.state;
        return(
            
            <div className='overflow-y-auto'> 
                
                <table className="" cellSpacing="0" id='historyTable'>
                        <thead className=''>
                            <tr className=''>
                                <th className=" ">ID</th>
                                <th className=" ">Name</th>
                                <th className=" ">Admin?</th>
                            </tr>
                        </thead>
                        <tbody className="">
                            {usersArr.map(function(data, i){
                                return(
                                    <tr className='' >
                                        <td key={i} className="">{data.id}</td>
                                        <td key={i} className="">{data.name}</td>
                                        <td key={i} className="">{this.ifAdmin(data.admin)}</td>
                                    </tr>
                                )
                            })}
                        </tbody>
                    </table>
                
            </div>
        )
    }
}

export default EditUsers;
