import React from 'react';

const Edit = ({ singleuser }) => {
    
    const onUpdateName = () => {
        //CREATE FETCH FUNCTION TO GET THE USER WE ARE LOOKING AT AND CHANGE THE NAME AS LONG AS NO ONE ELSE HAD THAT NAME
        const newname = document.getElementById('newusername').value;
        submitUpdate('name', newname); 
    }
    const onUpdatePassword = () => {
        //CREATE FETCH FUNCTION TO GET THE USER WE ARE LOOKING AT AND CHANGE THE NAME AS LONG AS NO ONE ELSE HAD THAT NAME
        const newpassword = document.getElementById('newpassword').value;
        submitUpdate('password', newpassword); 
    }
    const onUpdateAdmin = () => {
        //CREATE FETCH FUNCTION TO GET THE USER WE ARE LOOKING AT AND CHANGE THE NAME AS LONG AS NO ONE ELSE HAD THAT NAME
        const newadmin = document.getElementById('newadmin').value;
        submitUpdate('admin', newadmin); 
    }

    const submitUpdate = (type, data ) => {
        console.log(data)
        fetch(`http://localhost:3000/edit${type}`, {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({
            id: singleuser.id,
            change: data,
        })
    })
    .then(response => response.json())
    .then(updateduser =>{
        console.log(updateduser);
    })
    .catch(err => console.log(err));
    }


    return(
        <div className='shadow-5 w-50 center'>
            <div className=" ma3 pa2">
                <label> Username</label>
                <input id='newusername' type='text' placeholder={`${singleuser.name}`} />
                <input id='submitnewusername' type='button' value='Update new name' onClick={onUpdateName} />
            </div>
            <div className="ma3 pa2">
                <label> Password</label>
                <input id='newpassword' type='password' placeholder={`Enter new password here`} />
                <input id='submitnewpassword' type='button' value='Update new password' onClick={onUpdatePassword} />
            </div>
            <div className="ma3 pa2">
                <label> Admin user?</label>
                <input id='newpassword' type='checkbox' />
                <input id='submitnewpassword' type='button' value='Update admin user' onClick={onUpdateAdmin} />
            </div>
        </div>
    )
}

export default Edit;
