import React from 'react';

const Edit = ({ singleuser, onReload, route }) => {
    
    const onUpdate = () => {
        const newname = document.getElementById('inputupdate').value;
        submitUpdate(route, newname); 
    }

    const onUpdateadmin = () => {
        const box = document.getElementById('inputupdate');
        let newadmin = false;
        if (box.checked) {
            newadmin = true;
        }
        submitUpdate(route, newadmin); 
    }


    const submitUpdate = (type, data ) => {
        console.log(data)
        fetch(`http://localhost:3000/edit${type}`, {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({
            id: singleuser.id,
            change: data,
        })
        })
        .then(response => response.json())
        .then(updateduser =>{
            onReload('editusers')
        })
        .catch(err => console.log(err));
    }

    if (route !== 'admin'){
        return(
            <div className='shadow-5 w-50 center'>
                <main className="pa4 black-80">
                    <form className="measure center">
                        <div id="sign_up" className="ba b--transparent ph0 mh0">
                            <label className="f4 fw6 ph0 mh0">{`User: ${singleuser.name}`}</label>
                            <div className="mt3">
                                <label className="db fw6 lh-copy f6" >Update form </label>
                                <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100 ma2" type="text" name="Name"  id="inputupdate" placeholder={`Insert new ${route}`} />
                                <input className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" type="reset" value="Update " onClick={onUpdate} />
                            </div>
                        </div>
                    </form>
                </main>         


            </div>
        )
    } else {
        return(
            <div className='shadow-5 w-50 center'>
                <main className="pa4 black-80">
                    <form className="measure center">
                        <div id="sign_up" className="ba b--transparent ph0 mh0">
                            <label className="f4 fw6 ph0 mh0">{`User: ${singleuser.name}`}</label>
                            <div className="mt3">
                                <label className="db fw6 lh-copy f6" >Update form (Check box if you want user to have access to admin side)</label>
                                <label className="pa0 ma0 lh-copy f6 pointer"><input id='inputupdate' type="checkbox" /> Admin User?</label>
                                <input className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" type="reset" value="Update " onClick={onUpdateadmin} />
                            </div>
                        </div>
                    </form>
                </main>         


            </div>
        )
    }
}

export default Edit;
