import React from 'react';

const Error = () => {

    const changeback = (event) => {
        if (event.target.innerText !== '') {
            setTimeout(reset(),5000);
        }
    }

    const reset = () => {
        document.getElementById('error').innerText = '';
    }

    return (
        <div
            id = 'error'
            className='ma4'
            onChange= {changeback()}
        >

        </div>
    )
}

export default Error;
