import React, { Component } from 'react'
import Image from './image.jpg';
import QrReader from 'react-qr-reader';

class PunchForm extends Component {
    
    handleScan = (data) => {
        if (data) {
          console.log(data)
        }
      }
    handleError = (err) => {
        console.error(err)
    }

    render(){
    return(
        <div>
            <QrReader
                delay={300}
                onError={this.handleError}
                onScan={this.handleScan}
                style={{ width: '50%' }}
            />
        </div>    
    )
    }
}

export default PunchForm;



