import React from 'react';
import './History.css';
import ReactToExcel from 'react-html-table-to-excel';
import HistoryNav from './HistoryNav';


class History extends React.Component {
    constructor(){
        super();
        this.state = {
            adminlist: []
        }
      }

    
    get = () =>{fetch(`http://localhost:3000/loadusers`, {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({
            id: this.props.user.id
        })
    })
    .then(response => response.json())
    .then(puncharray =>{
        this.setState({array: puncharray});
    })
    .catch(err => console.log(err));
    }

    

    render() {
        
        const { historyarray, user } = this.props;
        console.log(historyarray)
        return(
            <div className="shadow-5 w-80 center">
                { user.admin === true
                    ?(<div> 
                        {/* Place drop down navigation to choose user specific user history and date range */}
                        <HistoryNav user={user} />
                    </div>
                    )
                    :<div></div>
                }
                <div className="mt4">
                    <table className="table-cont" cellSpacing="0" id='historyTable'>
                        <thead className=''>
                            <tr className=''>
                                <th className=" ">Location</th>
                                <th className=" ">In/Out</th>
                                <th className=" ">Date</th>
                                <th className=" ">Time</th>
                            </tr>
                        </thead>
                        <tbody className="">
                            {historyarray.map(function(data, i){
                                return(
                                    <tr className='' >
                                        <td key={i} className="">{data.location}</td>
                                        <td key={i} className="">{data.inout}</td>
                                        <td key={i} className="">{data.date}</td>
                                        <td key={i} className="">{data.time}</td>
                                    </tr>
                                )
                            })}
                        </tbody>
                    </table>
                </div>
                <ReactToExcel
                    className="b textc mt4 mb4 sspd pv2 input-reset ba b--black bg-transparent grow pointer f6 dib w-20 center" 
                    table='historyTable'
                    filename='HistoryFile'
                    sheet='Sheet 1'
                    buttonText='Export History'
                    />
            </div>
        )
    }
}


export default History;
