import React from 'react';


class EditUsers extends React.Component {
    constructor(){
        super();
        this.state = {
            usersArr: []
        }
      }
    
    loadArr = () =>{
        fetch(`http://localhost:3000/editusers`, {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                id: this.props.user.id,
                admin: this.props.user.admin,
            })
        })
        .then(response => response.json())
        .then(usersArray =>{
            console.log('good')
            this.setState({usersArr: usersArray})
           
        })
        .catch(err => console.log(err));
}

    
    render(){
        this.loadArr()
        const { useraArr } = this.state;
        return(
            
            <div className='overflow-y-auto'> 
                
                <p>hello</p>
                
                
                    <div>{ usersArr.map(function(data, i){
                return(
                    <div className='' >
                        <p key={i} className="">{data.id}</p>
                        <p key={i} className="">{data.name}</p>
                        <p key={i} className="">{data.admin}</p>
                    </div>
                )
            })}</div>
                
            </div>
        )
    }
}

export default EditUsers;
