import React from 'react';
import Submitbtn from '../Submitbtn';
import Inputbar from '../Inputbar';

class Manualpunch extends React.Component {
    constructor(){
        super();
        this.state = {
            locationarr: [],
            adminlist: [],
            punch: {},
            route: 'punch'
        }
    }

    getadminlist = () =>{
        fetch(`https://mikan-app-api.herokuapp.com/loadusers`, {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({
            id: this.props.user.id,
            admin: this.props.user.admin
        })
    })
    .then(response => response.json())
    .then(usersarray =>{
        this.setState({adminlist: usersarray});
    })
    .catch(err => console.log(err));
    }

    loadlocarr = () =>{
        fetch(`https://mikan-app-api.herokuapp.com/locationsload`, {
            method: 'GET',
            headers: {'Content-Type': 'application/json'},
        })
        .then(response => response.json())
        .then(locarr =>{
            if (locarr[0].id){
            this.setState({locationarr: locarr})
            } else {
                console.log(locarr)
            }
        })
        .catch(err => console.log(err));
    }

    onRouteChange = () => {
        const user = document.getElementById('select_user').value; 
        //Location variable is an array with place 0 being code and 1 being name
        const location = document.getElementById('select_location').value;
        const in_out = document.getElementById('select_inout').value; 
        const date = document.getElementById('date').value;
        const time = document.getElementById('time').value;

        fetch('https://mikan-app-api.herokuapp.com/recordpunch', {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                id: user,
                location: location[0],
                in_out: in_out,
                locationname: location[1],
                date: date,
                time: time

            })
        })
        .then(response => response.json())
        .then(punch => {
            // eslint-disable-next-line
            if (punch.id == this.props.user.id) {
                this.setState({route:'recipt', punch: punch });
                const button = document.getElementById('submit');
                button.disabled = true;
            } else {
                console.log('punch')
            }
        })
        .catch(err => console.log(err));


        console.log(location)
    }

    componentDidMount() {
        this.loadlocarr();
        this.getadminlist();
        console.log(this.state.adminlist);
    }

    render() {
        const { adminlist, locationarr } = this.state;

        return(
            <div>
                <article className="br3 ba b--black-10 mv4 w-100 mw6 shadow-5 center">
                    <main className="pa4 black-80">
                        <form id="manual_punch" className="ba b--transparent ph0 mh0">
                            <legend className="f1 fw6 ph0 mh0 center bb">Manual Punch</legend>
                            <div className='m4 w-70 center '>
                                <label className='b ssftn5' >User </label>
                                <select id='select_user' className='w-100 hover-bg-black hover-white bg-transparent b ba b--black' >
                                    {
                                        adminlist.map(function(data,i){
                                            return(
                                                //NEED TO ADD KEY VALUES THAT ARE UNIQUE
                                                <option className='pa2' value={data.id}>{data.name}</option>
                                            )
                                        })
                                    }
                                </select>
                            </div>
                            <div className='m4 w-70 center '>
                                <label className='b ssftn5' >Location </label>
                                <select id='select_location' className='w-100 hover-bg-black hover-white bg-transparent b ba b--black' >
                                    {
                                        locationarr.map(function(data,i){
                                            return(
                                                //NEED TO ADD KEY VALUES THAT ARE UNIQUE
                                                <option className='pa2 ' value={[data.code, data.name]}>{data.name}</option>
                                            )
                                        })
                                    }
                                </select>
                            </div>
                            <div className='m4 w-70 center '>
                                <label className='b ssftn5' >In or Out? </label>
                                <select id='select_inout' className='w-100 hover-bg-black hover-white bg-transparent b ba b--black' >
                                    <option className='pa2 ' value={'In'}>IN</option>
                                    <option className='pa2 ' value={'Out'}>OUT</option>
                                </select>
                            </div>

                            {/* <div className=' w-70 m4 center'>
                                <label className='b ssftn5' >Date: </label>
                                <input className='b ba b--black bg-transparent pointer ssftn5 dib hover-bg-black hover-white w-100' type="date" id="date" name="search-start" required pattern="[0-9]{4}-[0-9]{2}-[0-9]{2}"/>
                            </div> */}
                            <Inputbar label='Date' id='date' type='date' />
                            {/* <div className='w-70 m4 center '>
                                <label className='b ssftn5' >Time </label>
                                <input className='b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100' type="time" id="time" name="search-time" required/>
                            </div> */}
                            <Inputbar label='Time' id='time' type='time' />
                            <Submitbtn value='Add Punch' onClick={this.onRouteChange} className='w-30' />
                        </form>
                    </main>
                </article>
            </div>
        )
    }
}

export default Manualpunch;
