import React, { Component } from 'react';
import SignIn from './components/SignIn/SignIn';
import Navigation from './components/Navigation/Navigation';
import PunchForm from './components/PunchForm/PunchForm';
import Menu from './components/Menu/Menu';
import History from './components/History/History';
import PunchRecipt from './components/PunchForm/PunchRecipt';
import './App.css';

class App extends Component {
  constructor(){
    super();
    this.state = {
      route: 'signin',
      isSignedIn: false,
      user: {
        id: '',
        name: '',
      },
      punch: {
        in_out: '',
        location: '',
        date: ''
    }
  }}

  onSubmitInfo = (data) => {
    const { location, date, in_out } = data;
    this.setState({
      punch:{
        location: location,
        in_out: in_out,
        date: date
    }});
  }

  loadUser = (user) => {
    this.setState({user: {
      id: user.id,
      name: user.name
    }})
  }

  punchIn_Out = (punch) => {
    if (punch === 'In'){
      this.setState({punch:{in_out: 'IN'}})
    }
    if (punch === 'Out'){
      this.setState({punch:{in_out: 'OUT'}})
    }
  }

  onRouteChange = (route) => {
    if (route === 'punchform'){
      this.setState({route:'punchform'})
    }
    else if (route === 'punchrecipt'){
      this.setState({route:'punchrecipt'})
    }
    else if(route === 'home'){
      this.setState({
        isSignedIn: true, 
        route: route});
    } 
    else if(route === 'signin'){
      this.setState({
        isSignedIn: false,
        route: route});
    } 
    else {
      this.setState({route: route})
    }
  }

  render() {
    const { route, isSignedIn, punch, user } = this.state;
    return (
      <div className="App">
        <Navigation onRouteChange={this.onRouteChange} isSignedIn={isSignedIn}/>
        { route === 'home'
          ?<Menu onRouteChange={this.onRouteChange} punchIn_Out={this.punchIn_Out} />
          :(
            route === 'punchform'
            ?<PunchForm 
              punch={punch} 
              user={user}
              onRouteChange={this.onRouteChange}
              onSubmitInfo={this.onSubmitInfo} 
              />
            :(
              route === 'history'
              ?<History />
              :(
                route === 'signin'
                ?<SignIn onRouteChange={this.onRouteChange} loadUser={this.loadUser}/>
                :<PunchRecipt punch={punch} />
              )
            )
          )
        }
      </div>
    );
  }
}

export default App;
