import React from 'react';


class Register extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            registerName: '',
            registerPassword: '',
            registerAdmin: false

        }
    }

    onNameChange = (event) => {
        this.setState({registerName: event.target.value})
    }

    onPasswordChange = (event) => {
        this.setState({registerPassword: event.target.value})
    }

    onAdminChange = (event) => {
        if (event.checked === true){
            this.setState({registerAdmin: true })
        } else {
            this.setState({registerAdmin: false })
        }
    }




    render(){
    if (this.props.user.admin === true) {
    return (
        <div>
            <main class="pa4 black-80">
                <form class="measure center">
                    <div id="sign_up" class="ba b--transparent ph0 mh0">
                        <label class="f4 fw6 ph0 mh0">Register</label>
                        <div class="mt3">
                            <label class="db fw6 lh-copy f6" for="email-address">Name</label>
                            <input class="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="text" name="Name"  id="Name" onChange={this.onNameChange} />
                        </div>
                        <div class="mv3">
                            <label class="db fw6 lh-copy f6" for="password">Password</label>
                            <input class="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="password" name="password"  id="password" onChange={this.onPasswordChange} />
                        </div>
                        <label class="pa0 ma0 lh-copy f6 pointer"><input type="checkbox" che onClick={this.onAdminChange}/> Admin User?</label>
                    </div>
                    <div class="">
                        <input class="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" type="submit" value="Register User"/>
                    </div>
                </form>
            </main>     
        </div>
    )
    } else {
        return(
            <div>
                <p>You do not have access to this part of the site</p>
            </div>
        )
    }
    }
}

export default Register;
